import React, {useState, useEffect} from 'react';
import {Line} from 'react-chartjs-2';
import { firebase } from "./initFirebase";
import { useRouter, } from "next/router";

const db = firebase.database();

function Home() {
  const [time, setTime] = useState("Time");
  const [temp, setTemp] = useState("Temp");
  const [altitude, setAltitude] = useState("Altitude");
  const [yaw, setYaw] = useState("Yaw");
  const router = useRouter();

  return (
    <main>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          const launchRef = db.ref("launch");
          const newLaunchRef = launchRef.push();
          newLaunchRef.set({
            time,
            temp,
            altitude,
            yaw,
          });

        }}
      >
        <h1>Inserir dados</h1>

        <input
          placeholder="Tempo"
          type="number"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />

        <input
          placeholder="Temperatura"
          type="number"
          value={temp}
          onChange={(e) => setTemp(e.target.value)}
        />

        <input
          placeholder="Altitude"
          type="number"
          value={altitude}
          onChange={(e) => setAltitude(e.target.value)}
        />

        <input
          placeholder="Yaw"
          type="number"
          value={yaw}
          onChange={(e) => setYaw(e.target.value)}
        />

        <button type="submit">Confirmar</button>
      </form>
    </main>
  );

}
const App = () => {
  const launchRef = db.ref("launch");

  var ref = firebase.database().ref("launch");

  const [temperatureData, setTemperatureData] = useState({});
  const [altitudeData, setAltitudeData] = useState({});
  const [yawData, setYawData] = useState({});
  useEffect(() => {
    const loadData = async () => {





      const setDados = ({tempo,temperatura,altitude,yaw})=>{
        setTemperatureData({
          datasets: [
            {
              label: 'Temperatura',
              data: temperatura,
              backgroundColor: 'rgba(255, 99, 132, 1)',
              hoverBackgroundColor: "rgba(255, 99, 132, 1)",
              hoverBorderColor: "black",
              borderColor: 'rgba(255, 99, 132, 1)',
              borderWidth: 2
            }
          ], 
          labels: tempo
        });
        setAltitudeData({
          datasets: [
            {
              label: 'Altitude',
              data: altitude,
              backgroundColor: 'rgba(19, 240, 14, 1)',
              hoverBackgroundColor: 'rgba(19, 240, 14, 1)',
              hoverBorderColor: "black",
              borderColor: 'rgba(19, 240, 14, 1)',
              borderWidth: 2
            }
          ], 
          labels: tempo
        });
        setYawData({
          datasets: [
            {
              label: 'Yaw',
              data: yaw,
              backgroundColor: 'rgba(139, 0, 249, 1)',
              hoverBackgroundColor: 'rgba(139, 0, 249, 1)',
              hoverBorderColor: "black",
              borderColor: 'rgba(139, 0, 249, 1)',
              borderWidth: 2
            }
          ], 
          labels: tempo
        });
      } 
      firebase.database().ref().child("launch").on("value", function (snapshot) {
        snapshot.forEach(function(childSnapshot) {

         var tempo = Object.values(childSnapshot.val().time);
         var temperatura =childSnapshot.val().temp;
         var altitude =childSnapshot.val().altitude;
         var yaw =childSnapshot.val().yaw;

         setDados(tempo,temperatura,altitude,yaw)
        });

      });    
    }

    loadData();

    firebase.database().ref().child("launch").on("value", function (snapshot) {
        snapshot.forEach(function(childSnapshot) {

         var tempo = Object.values(childSnapshot.val().time);
         var temperatura =childSnapshot.val().temp;
         var altitude =childSnapshot.val().altitude;
         var yaw =childSnapshot.val().yaw;

         setDados(tempo,temperatura,altitude,yaw)

        });

      });

  }, [])

  const temperatureOptions = {
    elements: {
        point:{
            radius: 1
        }
    },
    scales: {
      y: {
          ticks: {
              callback: function(value, index, values) {
                  return value + "Â°C";
              }
          },
      }
    },
    responsive:false
  }
  const altitudeOptions = {
    elements: {
        point:{
            radius: 1
        }
    },
    scales: {
      y: {
          ticks: {
              callback: function(value, index, values) {
                  return value + "m";
              }
          },
      }
    },
    responsive:false
  }
  const yawOptions = {
    elements: {
        point:{
            radius: 1
        }
    },
    scales: {
      y: {
          ticks: {
              callback: function(value, index, values) {
                  return value + "";
              }
          },
      }
    },
    responsive:false
  }

  return <>
    <Line id="chart" width="1500" height="320" data={temperatureData} options={temperatureOptions}/> 
    <Line id="chart" width="1500" height="320" data={altitudeData} options={altitudeOptions}/> 
    <Line id="chart" width="1500" height="320" data={yawData} options={yawOptions}/> 
  </>
}

export {Home, App};
